import { useState, useEffect } from "react"
import axios from "axios"

export default function useAuth(code) {
    const [accessToken, setAccessToken] = userState()
    const [refreshToken, setRefreshToken] = userState()
    const [expiresInToken, setExpiresInToken] = userState() 

    useEffect(() => {
        axios.post("http//localhost:3001/login", {
            code,
        }).then(res => {
            console.log(res.data)
        })
    }, [code])
}
